"use strict";
var regenerate = require("regenerate"),
  template = require("lodash.template"),
  version = "8.0.0",
  get = function (a) {
    return require("unicode-" + version + "/" + a + "/code-points");
  },
  ID_Start = get("properties/ID_Start"),
  ID_Continue = get("properties/ID_Continue"),
  compileRegex = template(
    "/^(?:<%= identifierStart %>)(?:<%= identifierPart %>)*$/"
  ),
  generateES6Regex = function () {
    var a = regenerate(ID_Start).add("$", "_"),
      b = regenerate(ID_Continue).add("$", "_", "\u200c", "\u200d");
    return compileRegex({
      identifierStart: a.toString(),
      identifierPart: b.toString(),
    });
  };
console.log(generateES6Regex());
