function FontScaling() {
  var self = this;
  adm.htmlad.on("complete", function() {
    self.doScaling();
  });
  adm.htmlad.on("invrender", function() {
    self.doScaling();
  });
  window.addEventListener("scaleFonts", function() {
    self.doScaling();
  });
  window.addEventListener("txt_link_present", function() {
    self.doScaling();
  });
}
FontScaling.prototype.doScaling = function() {
  var self = this;
  Array.prototype.forEach.call(
    document.querySelectorAll('[fontscaling="true"]'),
    function(el) {
      if (el.innerText === "") {
        return false;
      }
      var singleLine = false,
        isChild = el.classList.contains("content");

      if (
        window.getComputedStyle(el) &&
        window.getComputedStyle(el)["white-space"] &&
        window.getComputedStyle(el)["white-space"] === "nowrap"
      ) {
        singleLine = true;
      }
      if (singleLine === false) {
        el.style["lineHeight"] =
          window.getComputedStyle(el)["line-height"].slice(0, -2) /
            window.getComputedStyle(el)["font-size"].slice(0, -2) +
          "em";
      }
      var count = 0,
        textPosition = null;

      if (!isChild) {
        if (el) {
          var childNode = el.querySelectorAll("div, span")[0];
          if (childNode && childNode.dataset && childNode.dataset.algn) {
            textPosition = childNode.dataset.algn;
            childNode.dataset.algn = undefined;
            childNode.setAttribute("data-algn", undefined);
          }
        }

        while (
          (count < 1000 && !singleLine && el.scrollHeight > el.offsetHeight) ||
          el.scrollWidth > el.offsetWidth
        ) {
          count++;
          var fontSize = el.style["fontSize"];
          el.style["fontSize"] = (
            (parseFloat(self.findNumbersInString(fontSize)[0]) - 0.1).toFixed(
              1
            ) + self.findLettersInString(fontSize)[0]
          ).replace(".0", "");
          el.style["lineHeight"] =
            window.getComputedStyle(el)["line-height"].slice(0, -2) /
              window.getComputedStyle(el)["font-size"].slice(0, -2) +
            "em";
        }
        if (textPosition) {
          childNode.dataset.algn = textPosition;
          childNode.setAttribute("data-algn", textPosition);
        }
      } else {
        var transformStyle = null;
        if (
          el.id &&
          el.id.length > 0 &&
          adm &&
          adm.htmlad &&
          adm.htmlad.params &&
          adm.htmlad.params[el.id] &&
          !el.classList.contains("swapped") &&
          !el.classList.contains("hoverparam")
        ) {
          if (
            el.dataset &&
            el.dataset.ohtml &&
            el.dataset.ohtml.indexOf("<div") > -1
          ) {
            el.innerHTML = el.dataset.ohtml;
            el.childNodes[0].innerHTML = adm.htmlad.params[el.id];
            if (el.childNodes[0] && el.childNodes[0].style) {
              el.childNodes[0].style.pointerEvents = "none";
            }
            if (
              el.childNodes[0] &&
              el.childNodes[0].style &&
              el.childNodes[0].style.transform
            ) {
              transformStyle = el.childNodes[0].style.transform;
              el.childNodes[0].style.transform = null;
            }
          } else {
            el.innerHTML = adm.htmlad.params[el.id];
          }
        }
        if (!el.childNodes[0] || !el.childNodes[0].style) {
          el.innerHTML =
            '<div style="pointer-events:none;">' + el.innerHTML + "</div>";
        }
        if (el.childNodes[0].dataset && el.childNodes[0].dataset.algn) {
          textPosition = el.childNodes[0].dataset.algn;
          el.childNodes[0].dataset.algn = undefined;
          el.childNodes[0].setAttribute("data-algn", undefined);
        }
        while (
          (count < 1000 && !singleLine && el.scrollHeight > el.offsetHeight) ||
          (el.scrollWidth > el.offsetWidth &&
            el.innerHTML.indexOf("transform: skew(") === -1)
        ) {
          count++;
          var fontSize =
            el.childNodes[0].style["fontSize"] ||
            window.getComputedStyle(el.childNodes[0])["fontSize"];
          el.childNodes[0].style["fontSize"] = (
            (parseFloat(self.findNumbersInString(fontSize)[0]) - 0.1).toFixed(
              1
            ) + self.findLettersInString(fontSize)[0]
          ).replace(".0", "");

          var lineHeight = el.childNodes[0].style["lineHeight"];
          el.childNodes[0].style["lineHeight"] =
            window.getComputedStyle(el)["line-height"].slice(0, -2) /
              window.getComputedStyle(el)["font-size"].slice(0, -2) +
            "em";
        }
        if (transformStyle) {
          el.childNodes[0].style.transform = transformStyle;
        }
        if (textPosition) {
          el.childNodes[0].dataset.algn = textPosition;
          el.childNodes[0].setAttribute("data-algn", textPosition);
        }
      }
    }
  );
};
FontScaling.prototype.findNumbersInString = function(string) {
  return string.match(/[.0-9]*\d/g) || [0];
};
FontScaling.prototype.findLettersInString = function(string) {
  return string.match(/([^0-9^\u002E\u002D])+/g) || "";
};

adm.cs.AABDirectives.FontScaling = FontScaling;



function ConditionalEvents() {
  var self = this;

  if (!adm.cs.aabAdmEvents) {
    adm.cs.aabAdmEvents = [];
  }
  self.eventsFired = [];

  adm.htmlad.on("complete", function() {
    self.overlayStartEvent();
    self.overlayEndEvent();
    self.timedEventDelay();
    self.handleOfferFallback();
    self.handleNonPresentParams();
    self.paramValueEvents();
  });
}

ConditionalEvents.prototype.timedEventDelay = function() {
  var self = this;

  Object.keys(adm.htmlad.params).forEach(function(param) {
    if (param.indexOf("txt_timedevent_") > -1) {
      setTimeout(function() {
        self.fireEvent(param);
      }, parseInt(adm.htmlad.params[param]) * 1000);
    }
  });
};

ConditionalEvents.prototype.overlayStartEvent = function() {
  var self = this;
  if (
    adm.htmlad.params.hasOwnProperty("txt_adsettingsoverlaystart") &&
    parseInt(adm.htmlad.params["txt_adsettingsoverlaystart"])
  ) {
    var startTime = adm.htmlad.params["txt_adsettingsoverlaystart"];
    setTimeout(function() {
      self.fireEvent("video_overlay_start");
    }, startTime * 1000);
  }
};

ConditionalEvents.prototype.overlayEndEvent = function() {
  var self = this;
  if (
    adm.htmlad.params.hasOwnProperty("txt_adsettingsoverlayend") &&
    parseInt(adm.htmlad.params["txt_adsettingsoverlayend"])
  ) {
    var endTime = adm.htmlad.params["txt_adsettingsoverlayend"];
    setTimeout(function() {
      self.fireEvent("video_overlay_end");
    }, endTime * 1000);
  }
};

ConditionalEvents.prototype.handleOfferFallback = function() {
  var self = this;
  if (
    adm.htmlad.params["offer_id"] &&
    adm.htmlad.params["offer_id"].indexOf("FILLER") === 0
  ) {
    self.fireEvent("offer_fallback");
  }
};

ConditionalEvents.prototype.handleNonPresentParams = function() {
  var self = this;
  Array.prototype.forEach.call(
    adm.cs.querySelectorAll(
      '[id^="_txt"],[id^="txt_"],[id^="_img"],[id^="img_"],[id^="_inv"],[id^="inv_"]'
    ),
    function(el) {
      var id = el.id,
        eventName = id + "_notpresent",
        eventNamePresent = id + "_present";

      if (!adm.htmlad.params[id] || adm.htmlad.params[id] === "") {
        self.fireEvent(eventName);
      } else {
        self.fireEvent(eventNamePresent);
      }
    }
  );
};

ConditionalEvents.prototype.paramValueEvents = function() {
  var self = this;
  Object.keys(adm.htmlad.params).forEach(function(param) {
    if (adm.htmlad.params[param] !== "") {
      var eventName = "pv-" + param + "|" + adm.htmlad.params[param];
      self.fireEvent(eventName);
      if (
        self.eventsFired &&
        self.eventsFired.indexOf(param + "_present") === -1
      ) {
        self.fireEvent(param + "_present");
      }
    }
  });
};

ConditionalEvents.prototype.fireEvent = function(eventName) {
  var self = this;
  adm.cs.aabAdmEvents.push(eventName);
  adm.cs.fireEvent(eventName);
  if (self.eventsFired) {
    self.eventsFired.push(eventName);
  }
  self.log("AAB heard ADM event: " + eventName);
};

ConditionalEvents.prototype.log = function(input) {
  if (adm && adm.ad && adm.ad.trace) {
    adm.ad.trace(input);
  } else {
    console.log(input);
  }
};

adm.cs.AABDirectives.ConditionalEvents = ConditionalEvents;



