Inventory.prototype.removeElementsWithClass = function(parent, className) {
  Array.prototype.forEach.call(
    adm.cs.querySelectorAll("." + className, parent),
    function(el) {
      el.parentNode.removeChild(el);
    }
  );
};

Inventory.prototype.paymentInfo = function(vehicle, adDiv) {
  var self = this,
    paymentTypes = ["lease", "purchase"],
    elementPlacement = {
      leaseX:
        (adDiv.querySelector(".lease") &&
          adDiv.querySelector(".lease").style &&
          adDiv.querySelector(".lease").style.left) ||
        "0px",
      leaseY:
        (adDiv.querySelector(".lease") &&
          adDiv.querySelector(".lease").style &&
          adDiv.querySelector(".lease").style.top) ||
        "0px",
      purchaseX:
        (adDiv.querySelector(".purchase") &&
          adDiv.querySelector(".purchase").style &&
          adDiv.querySelector(".purchase").style.left) ||
        "0px",
      purchaseY:
        (adDiv.querySelector(".purchase") &&
          adDiv.querySelector(".purchase").style &&
          adDiv.querySelector(".purchase").style.top) ||
        "0px"
    };

  if (!adm.htmlad.param("payment_info")) {
    return false;
  }

  if (vehicle.paymentinfo) {
    if (!vehicle.paymentinfo.lease) {
      paymentTypes = paymentTypes.filter(function(item) {
        if (item !== "lease") {
          return item;
        }
      });
      self.removeElementsWithClass(adDiv, "lease");
    } else {
      self.removeElementsWithClass(adDiv, "msrp");
    }

    if (!vehicle.paymentinfo.finance) {
      paymentTypes = paymentTypes.filter(function(item) {
        if (item !== "purchase") {
          return item;
        }
      });
      self.removeElementsWithClass(adDiv, "purchase");
    } else {
      self.removeElementsWithClass(adDiv, "msrp");
    }

    self.multiPaymentFallback(elementPlacement, paymentTypes, adDiv);

    if (
      (vehicle.paymentinfo.lease &&
        vehicle.paymentinfo.lease.disclaimerterms &&
        vehicle.paymentinfo.lease.disclaimerterms.length > 0) ||
      (vehicle.paymentinfo.finance &&
        vehicle.paymentinfo.finance.disclaimerterms &&
        vehicle.paymentinfo.finance.disclaimerterms.length > 0)
    ) {
      Array.prototype.forEach.call(
        adm.cs.querySelectorAll(".legal button", adDiv),
        function(el) {
          el.addEventListener("click", function(event) {
            self.prepAndPopulateLegalText(vehicle);
            adm.cs.hasInteractionOccurred = true;
            event.stopPropagation();
          });
        }
      );
    } else {
      self.removeElementsWithClass(adDiv, "legal");
    }
  } else {
    Array.prototype.forEach.call(
      adm.cs.querySelectorAll(".lease, .purchase, .legal", adDiv),
      function(el) {
        el.parentNode.removeChild(el);
      }
    );
  }
};

Inventory.prototype.multiPaymentFallback = function(
  elementPlacement,
  paymentTypes,
  adDiv
) {
  if (
    document.querySelector("#inv_container") &&
    document.querySelector("#inv_container").dataset &&
    document.querySelector("#inv_container").dataset.invmultipaymentfallback
  ) {
    if (
      document.querySelector("#inv_container").dataset
        .invmultipaymentfallback === "purchaseXtoLeaseX"
    ) {
      if (paymentTypes.length === 1) {
        if (paymentTypes[0] === "purchase") {
          Array.prototype.forEach.call(
            adm.cs.querySelectorAll(".purchase", adDiv),
            function(el) {
              el.style.left = elementPlacement.leaseX;
            }
          );
        }
      }
    }

    if (
      document.querySelector("#inv_container").dataset
        .invmultipaymentfallback === "purchaseXYtoLeaseXY"
    ) {
      if (paymentTypes.length === 1) {
        if (paymentTypes[0] === "purchase") {
          Array.prototype.forEach.call(
            adm.cs.querySelectorAll(".purchase", adDiv),
            function(el) {
              el.style.left = elementPlacement.leaseX;
              el.style.top = elementPlacement.leaseY;
            }
          );
        }
      }
    }

    if (
      document.querySelector("#inv_container").dataset
        .invmultipaymentfallback === "leaseXtoPurchaseX"
    ) {
      if (paymentTypes.length === 1) {
        if (paymentTypes[0] === "lease") {
          Array.prototype.forEach.call(
            adm.cs.querySelectorAll(".lease", adDiv),
            function(el) {
              el.style.left = elementPlacement.purchaseX;
            }
          );
        }
      }
    }

    if (
      document.querySelector("#inv_container").dataset
        .invmultipaymentfallback === "leaseXYtoPurchaseXY"
    ) {
      if (paymentTypes.length === 1) {
        if (paymentTypes[0] === "lease") {
          Array.prototype.forEach.call(
            adm.cs.querySelectorAll(".lease", adDiv),
            function(el) {
              el.style.left = elementPlacement.purchaseX;
              el.style.top = elementPlacement.purchaseY;
            }
          );
        }
      }
    }
  }
};

