function EventDrivenChanges() {
  var self = this;
  self.changes = [];

  Array.prototype.forEach.call(
    adm.cs.querySelectorAll("[eventdrivenchanges]"),
    function(el, i) {
      var options = self.processOptionsString(
        el.getAttribute("eventdrivenchanges")
      );

      options.forEach(function(option) {
        var argument = option.argument;
        self.changes.push({
          element: el,
          change: option.change,
          event: option.event,
          x: decodeURIComponent(argument[0] || ""),
          y: decodeURIComponent(argument[1] || ""),
          condition: option.condition,
          onlyWhenAnimationDisabled: option.onlyWhenAnimationDisabled
        });
      });
    }
  );

  adm.htmlad.on("complete", function() {
    self.eventDriven(self.changes);
  });
  if (adm.cs.aabAdmEvents && adm.cs.aabAdmEvents.indexOf("complete") > -1) {
    self.eventDriven(self.changes);
  }
}

EventDrivenChanges.prototype.isAnimationEnabled = function() {
  return (
    (adm && adm.htmlad && adm.htmlad.cssAnimationSupport === true) || false
  );
};

EventDrivenChanges.prototype.processOptionsString = function(optionsString) {
  var self = this;
  var result = [],
    optionCondition = "",
    options = optionsString.split(";");
  for (var i = 0; i < options.length; i++) {
    var option = options[i];
    var onlyWhenAnimationDisabled = option.indexOf("~") === 0;
    if (option.indexOf("*") > -1) {
      optionCondition = option.split("*")[0].replace("~", "");
      option = option.split("*")[1];
    }
    if (option && option.length > 0) {
      var optionChange = option.split("@")[0].replace("~", ""),
        optionDriver = option.split("@")[1].split(":")[0],
        optionDriverArgument = option
          .split("@")[1]
          .split(":")[1]
          .split(",");

      result.push({
        condition: optionCondition,
        onlyWhenAnimationDisabled: onlyWhenAnimationDisabled,
        change: optionChange || "",
        event: optionDriver || "",
        argument: optionDriverArgument || []
      });
    }
  }
  return result;
};

EventDrivenChanges.prototype.doChange = function(thischange) {
  var self = this;
  if (
    thischange.onlyWhenAnimationDisabled === true &&
    self.isAnimationEnabled() === true
  ) {
    return false;
  }
  if (thischange.condition && !self.conditionIsMet(thischange.condition)) {
    return false;
  }

  if (thischange.element.classList.contains("edc-fallback")) {
    if (adm.htmlad.params._disable_fallback_events_override) {
      if (adm.htmlad.params._disable_fallback_events_override === "false") {
        return false;
      }
    } else {
      return false;
    }
  }
  if (thischange.event.indexOf("fallback") > -1) {
    thischange.element.classList.add("edc-fallback");
  }

  if (thischange.change === "move") {
    thischange.element.style.left = thischange.x;
    thischange.element.style.top = thischange.y;
  }
  if (thischange.change === "show") {
    thischange.element.style.display = "block";
    thischange.element.style.opacity = "1";
    thischange.element.style.pointerEvents = "auto";
  }
  if (thischange.change === "hide") {
    thischange.element.style.display = "none";
    thischange.element.style.opacity = "0";
    thischange.element.style.pointerEvents = "none";
  }
  if (thischange.change === "swap text color") {
    thischange.element.style.color = thischange.x;
  }
  if (thischange.change === "swap bg color") {
    thischange.element.children[0].style.background = thischange.x;
  }
  if (thischange.change === "swap text") {
    var text = thischange.x;

    Object.keys(adm.htmlad.params || []).forEach(function(param) {
      text = text.replace("[" + param + "]", adm.htmlad.params[param]);
      if (text.trim() === ",") {
        text = "";
      }
      if (text[text.length - 1] === ",") {
        text = text.replace(text, text.slice(0, -1));
      }
    });
    var el = thischange.element.children[0];
    el.innerHTML = text;
    el.classList.add("swapped");

    if (el && el.attributes["data-ohtml"] && el.attributes["data-hhtml"]) {
      el.setAttribute("data-ohtml", text);
      el.setAttribute("data-hhtml", text);
    }

    adm.cs.fireEvent("scaleFonts");
  }
};

EventDrivenChanges.prototype.conditionIsMet = function(condition) {
  var self = this,
    result = true;

  condition = condition.split(":");
  if (condition.length === 4) {
    var type = condition[0],
      key = condition[1].replace(/ /g, "").split(","),
      operator = condition[2],
      value = condition[3].split(",");

    key.forEach(function(param, index) {
      if (!value[index]) {
        result = false;
        return false;
      }

      if (type === "if param") {
        if (adm.htmlad.param(param)) {
          if (operator === "is") {
            if (adm.htmlad.param(param) !== value[index]) {
              result = false;
            }
          }
          if (operator === "is not") {
            if (adm.htmlad.param(param) == value[index]) {
              result = false;
            }
          }
        } else {
          result = false;
        }
      } else {
        result = false;
      }
    });
  } else {
    result = false;
  }
  return result;
};

EventDrivenChanges.prototype.eventDriven = function(changes) {
  var self = this;
  changes.forEach(function(thischange) {
    window.addEventListener(thischange.event, function() {
      self.log(
        "AAB, Event Driven Changes Directive | Event heard: " + thischange.event
      );
      self.doChange(thischange);
    });
    self.log("AAB, Event Driven Changes | Event bound: " + thischange.event);
    if (
      adm.cs.aabAdmEvents &&
      adm.cs.aabAdmEvents.indexOf(thischange.event) > -1
    ) {
      self.log(
        "AAB, Event Driven Changes Directive | Event heard: " + thischange.event
      );
      self.doChange(thischange);
    }
  });
};

EventDrivenChanges.prototype.log = function(input) {
  if (adm && adm.ad && adm.ad.trace) {
    adm.ad.trace(input);
  } else {
    console.log(input);
  }
};

adm.cs.AABDirectives.EventDrivenChanges = EventDrivenChanges;



function Colors() {
  var colors =
      (document.querySelector(".ad") &&
        document.querySelector(".ad").dataset &&
        document.querySelector(".ad").dataset.colors &&
        document.querySelector(".ad").dataset.colors.split(",")) ||
      [],
    self = this;

  if (colors.length === 0 || !adm.htmlad.params["colors"]) {
    return false;
  }

  var styleblock = document.createElement("style");
  colors.forEach(function(color, index) {
    self.updateColor(color, index);
    self.updateColor(self.hexToRgb(color, true), index);
    self.updateColor(self.hexToRgb(color), index);
    styleblock.innerHTML +=
      '[adm-color="' + index + '"] { color: ' + adm.htmlad.color(index) + " }";
  });
  document.head.appendChild(styleblock);
}

Colors.prototype.updateColor = function(oldColor, newColorIndex) {
  Array.prototype.forEach.call(adm.cs.querySelectorAll("style"), function(
    styleblock
  ) {
    if (styleblock && styleblock.innerHTML.indexOf("invpag") > -1) {
      styleblock.innerHTML = styleblock.innerHTML.replace(
        oldColor,
        adm.htmlad.color(newColorIndex)
      );
    }
  });

  Array.prototype.forEach.call(
    adm.cs.querySelectorAll(
      '[style*="' + oldColor + '"],[hover*="' + oldColor + '"]'
    ),
    function(el) {
      if (
        el.getAttribute("colors") ||
        (el.parentNode &&
          el.parentNode.getAttribute("colors") &&
          el.parentNode.classList.contains("frame") === false)
      ) {
        var applicableProperties =
          el.getAttribute("colors") || el.parentNode.getAttribute("colors");
        applicableProperties = applicableProperties.split(",");
        applicableProperties.forEach(function(property) {
          if (property.indexOf("hover:") === 0) {
            if (el.getAttribute("hover")) {
              property = property.replace("hover:", "");
              el.setAttribute(
                "hover",
                el
                  .getAttribute("hover")
                  .replace(
                    new RegExp(property + ": " + oldColor, "g"),
                    property + ": " + adm.htmlad.color(newColorIndex)
                  )
                  .replace(
                    property + ": " + oldColor,
                    property + ": " + adm.htmlad.color(newColorIndex)
                  )
              );
            }
          } else {
            el.style.cssText = el.style.cssText
              .replace(
                property + ": " + oldColor,
                property + ": " + adm.htmlad.color(newColorIndex)
              )
              .replace(
                property + ": none " + oldColor,
                property + ": none " + adm.htmlad.color(newColorIndex)
              );
          }
        });
      }
    }
  );
};

Colors.prototype.hexToRgb = function(hex, a) {
  hex = hex.replace("#", "");
  var r = parseInt(hex.substring(0, 2), 16),
    g = parseInt(hex.substring(2, 4), 16),
    b = parseInt(hex.substring(4, 6), 16);
  if (a === true) {
    return "rgba(" + r + ", " + g + ", " + b + ", 1)";
  } else {
    return "rgb(" + r + ", " + g + ", " + b + ")";
  }
};
adm.cs.AABDirectives.Colors = Colors;



